getTracks:
query getTracks($ids: [ID!]!, $withReleases: Boolean = false, $withArtists: Boolean = false) {
  getTracks(ids: $ids) {
    id
    title
    searchTitle
    position
    duration
    availability
    artistTemplate
    condition
    explicit
    lyrics
    zchan
    collectionItemData {
      itemStatus
    }
    artists @include(if: $withArtists) {
      id
      title
      searchTitle
      description
      hasPage
      image {
        src
        palette
        paletteBottom
      }
      secondImage {
        src
        palette
        paletteBottom
      }
      animation {
        artistId
        effect
        image
        background {
          type
          image
          color
          gradient
        }
      }
    }
    release @include(if: $withReleases) {
      id
      title
      searchTitle
      type
      date
      image {
        src
        palette
        paletteBottom
      }
      genres {
        id
        name
        shortName
      }
      label {
        id
        title
      }
      availability
      artistTemplate
    }
    hasFlac
  }
}

getStream:
query getStream($ids: [ID!]!) {
  mediaContents(ids: $ids) {
    ... on Track {
      stream {
        expire
        expireDelta
        high
        mid
      }
    }
    ... on Episode {
      stream {
        expire
        expireDelta
        high
        mid
      }
    }
    ... on Chapter {
      stream {
        expire
        expireDelta
        high
        mid
      }
    }
  }
}

getPlaylistTracks:
query getPlaylistTracks($id: ID!, $limit: Int = 5, $offset: Int = 0) {
  playlistTracks(id: $id, limit: $limit, offset: $offset) {
    artistTemplate
    artistNames
    artists {
      id
      title
    }
    hasFlac
    zchan
    lyrics
    title
    searchTitle
    explicit
    credits
    availability
    position
    duration
    id
    condition
    release {
      id
      genres {
        name
      }
      title
      image {
        src
        palette
        palette_bottom: paletteBottom
      }
    }
    searchTitle
    title
  }
}

getReleases:
query getReleases(
  $ids: [ID!]!
  $withTracks: Boolean = false
  $withArtists: Boolean = false
  $withLabel: Boolean = false
  $withRelated: Boolean = false
  $relatedLimit: Int = 100
) {
  getReleases(ids: $ids) {
    id
    title
    searchTitle
    type
    date
    genres {
      id
      name
      shortName
    }
    image {
      src
      palette
      paletteBottom
    }
    artists @include(if: $withArtists) {
      id
      title
    }
    tracks @include(if: $withTracks) {
      id
      title
      searchTitle
      duration
      position
      availability
      artistTemplate
      condition
      explicit
      lyrics
      hasFlac
      zchan
      artists {
        id
        title
      }
    }
    label @include(if: $withLabel) {
      id
      title
      searchTitle
      description
      image {
        src
        palette
        paletteBottom
      }
      hasImage
    }
    related(limit: $relatedLimit) @include(if: $withRelated) {
      id
      title
      searchTitle
      type
      date
      genres {
        id
        name
        shortName
      }
      image {
        src
        palette
        paletteBottom
      }
      artists {
        id
        title
      }
      availability
      artistTemplate
    }
    availability
    artistTemplate
  }
}
